package com.bnool.anishop.services.manage.account.impl;import com.bnool.anishop.core.ServersManager;import com.bnool.anishop.services.manage.account.AccountService;import com.bnool.anishop.services.manage.account.bean.Account;import com.bnool.anishop.services.manage.account.dao.AccountDao;import org.apache.commons.lang.StringUtils;public class AccountServiceImpl extends ServersManager<Account> implements		AccountService {	private AccountDao accountDao;	public void setAccountDao(AccountDao accountDao) {		this.accountDao = accountDao;	}		/**	 * 批量删除	 * 	 * @param ids	 * @return	 */	public int deletes(String[] ids) {		if (ids == null || ids.length == 0) {			throw new NullPointerException("id不能全为空！");		}				for (int i = 0; i < ids.length; i++) {			if(StringUtils.isBlank(ids[i])){				throw new NullPointerException("id不能为空！");			}			accountDao.deleteById(Integer.parseInt(ids[i]));		}		return 0;	}	}