package com.bnool.anishop.services.manage.orderdetail.impl;import com.bnool.anishop.core.ServersManager;import com.bnool.anishop.services.manage.orderdetail.OrderdetailService;import com.bnool.anishop.services.manage.orderdetail.bean.Orderdetail;import com.bnool.anishop.services.manage.orderdetail.dao.OrderdetailDao;import com.bnool.anishop.web.action.manage.report.ReportInfo;import java.util.List;public class OrderdetailServiceImpl extends ServersManager<Orderdetail>		implements OrderdetailService {	private OrderdetailDao orderdetailDao;	public void setOrderdetailDao(OrderdetailDao orderdetailDao) {		this.orderdetailDao = orderdetailDao;	}	@Override	public List<ReportInfo> reportProductSales(Orderdetail orderdetail) {		return orderdetailDao.reportProductSales(orderdetail);	}}