package com.bnool.anishop.web.action.manage.gift;import com.bnool.anishop.core.BaseAction;import com.bnool.anishop.core.ManageContainer;import com.bnool.anishop.core.system.bean.User;import com.bnool.anishop.services.manage.gift.GiftService;import com.bnool.anishop.services.manage.gift.bean.Gift;import org.apache.commons.lang.StringUtils;public class GiftAction extends BaseAction<Gift> {	private static final long serialVersionUID = 1L;	private GiftService giftService;	public GiftService getGiftService() {		return giftService;	}	protected void selectListAfter() {		pager.setPagerUrl("gift!selectList.action");	}	public void setGiftService(GiftService giftService) {		this.giftService = giftService;	}	public Gift getE() {		return this.e;	}	public void prepare() throws Exception {		if (this.e == null) {			this.e = new Gift();		}	}	public void insertAfter(Gift e) {		e.clear();	}		@Override	public String insert() throws Exception {		User user = (User)getSession().getAttribute(ManageContainer.manage_session_user_info);		e.setCreateAccount(user.getUsername());		return super.insert();	}		@Override	public String update() throws Exception {		User user = (User)getSession().getAttribute(ManageContainer.manage_session_user_info);		e.setUpdateAccount(user.getUsername());		return super.update();	}		/**	 * 查看商品赠品的基本信息	 * @return	 */	public String show(){		if(StringUtils.isBlank(e.getId())){			throw new NullPointerException("id不能为空！");		}				e = giftService.selectById(e.getId());		return super.show;	}}